mapper.update:
UPDATE
	TR_REV_REPORT
SET
	REPORT_HANDEL = #{report_handel}
	, HANDEL_CONT = #{handel_cont}
	, ANS_YN = 'Y'
WHERE
	REPORT_IDX = ${report_idx}

UPDATE
	TR_TRA_REPORT
SET
	REPORT_HANDEL = #{report_handel}
	, HANDEL_CONT = #{handel_cont}
	, ANS_YN = 'Y'
WHERE
	REPORT_IDX = ${report_idx}

mapper.insert:
INSERT INTO
